  */
object 分类算法_SVMWithSGD_支持向量机 {
  val logger = LoggerFactory.getLogger(分类算法_SVMWithSGD_支持向量机.getClass)
  Logger.getLogger("org").setLevel(Level.WARN)

  def main(args: Array[String]) {
    val spark = SparkSession.builder().appName(TF_IDF.getClass.getName).master("local[1]").getOrCreate()
    val sc = spark.sparkContext
    sc.setLogLevel("WARN")

    test(spark, sc)
    //    test2(spark, sc)
  }

  def test(spark: SparkSession, sc: SparkContext): Unit = {
    // 加载和解析数据文件
    val data = getTrainData(sc)
    val parsedData = data.map { line =>
      val parts = line.split(' ')
      LabeledPoint(parts(0).toDouble, Vectors.dense(parts.tail.map(x => x.toDouble)))
    }

    /** 设置迭代次数并进行进行训练 */
    val numIterations = 1000
    val model = SVMWithSGD.train(parsedData, numIterations)


    /** 统计分类错误的样本比例 */
    model.clearThreshold()
    val scoreAndLabels = parsedData.map { point =>
      val score = model.predict(point.features)
      (score, point.label)
    }
    scoreAndLabels.foreach(println)
    println(s"weights==" + model.weights) //不同feather的权重


    /** 如果设置了阈值，则会把大于阈值的结果当成正预测，小于阈值的结果当成负预测 */
    model.setThreshold(0.0)
    scoreAndLabels.foreach(println)

    /** 最后，我们构建评估矩阵，把模型预测的准确性打印出来 */
    val metrics = new BinaryClassificationMetrics(scoreAndLabels)
    val auROC = metrics.areaUnderROC()
    println("Area under ROC = " + auROC)
  }

