  import org.apache.spark.SparkContext
  import org.apache.spark.mllib.classification.SVMWithSGD
  import org.apache.spark.mllib.evaluation.BinaryClassificationMetrics
  import org.apache.spark.mllib.linalg.Vectors
  import org.apache.spark.mllib.regression.LabeledPoint

  def test2(spark: SparkSession, sc: SparkContext): Unit = {
    val data = sc.textFile("C:\\Users\\sk-qianxiao\\Desktop\\iris_libsvm.txt")

    val parsedData = data.map {
      line => {
        val parts = line.split(' ')
        LabeledPoint(parts(0).toDouble, Vectors.dense(parts.tail.map(x => x.toDouble)))
      }
    }

    /** 因为SVM只支持2分类，所以我们要进行一下数据抽取，这里我们通过filter过滤掉第2类的数据，只选取第0类和第1类的数据。然后，我们把数据集划分成两部分，其中训练集占60%，测试集占40% */
    val splits = parsedData.filter { point => point.label != 2 }.randomSplit(Array(0.6, 0.4), seed = 11L)
    val training = splits(0).cache()
    val test = splits(1)


    /** 通过训练集构建模型SVMWithSGD */
    val numIterations = 1000
    val model = SVMWithSGD.train(training, numIterations)
    println(s"getThreshold==" + model.getThreshold)
    println(s"weights==" + model.weights) //权重
    println(s"intercept==" + model.intercept)
    println(s"model.toString==" + model)

    model.clearThreshold()
    val scoreAndLabels = test.map { point =>
      val score = model.predict(point.features)
      (score, point.label)
    }
    scoreAndLabels.foreach(println)

    /** 如果设置了阈值，则会把大于阈值的结果当成正预测，小于阈值的结果当成负预测 */
    model.setThreshold(0.0)
    scoreAndLabels.foreach(println)

    /** 最后，我们构建评估矩阵，把模型预测的准确性打印出来 */
    val metrics = new BinaryClassificationMetrics(scoreAndLabels)
    val auROC = metrics.areaUnderROC()
    println("Area under ROC = " + auROC)

    /** 其中， SVMWithSGD.train() 方法默认的通过把正则化参数设为1来执行来范数。如果我们想配置这个算法，可以通过创建一个新的 SVMWithSGD对象然后调用他的setter方法来进行重新配置。
      * 下面这个例子，我们构建了一个正则化参数为0.1的L1正则化SVM方法 ，然后迭代这个训练算法2000次
      */
